SELECT YEAR(T1.SALES_DATE) as YEAR, MONTH(T1.SALES_DATE) as MONTH, GENDER, COUNT(DISTINCT T1.USER_ID) as USERS FROM ONLINE_SALE T1 JOIN USER_INFO T2 ON T1.USER_ID = T2.USER_ID WHERE T2.GENDER IS NOT NULL GROUP BY YEAR(T1.SALES_DATE), MONTH(T1.SALES_DATE), GENDER ORDER BY YEAR(T1.SALES_DATE), MONTH(T1.SALES_DATE), GENDER